package learningjava;

public class MultiplicationTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		 int num = 2;
	        for(int i = 10; i >=1; i--)
	        {
	            System.out.printf("%d * %d = %d \n", num, i, num * i);
	        }
	}

}
