package learningjava;

public class RelationalOperator {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		  int a = 111;
	      int b = 22;

	      System.out.println("a == b = " + (a == b) );
	      System.out.println("a != b = " + (a != b) );
	      System.out.println("a > b = " + (a > b) );
	      System.out.println("a < b = " + (a < b) );
	      System.out.println("b >= a = " + (b >= a) );
	      System.out.println("b <= a = " + (b <= a) );
	}

}
